        return $this;
    }

    public function setCodigoBanco($codigo)
    {
        $this->codigoBanco = $codigo;
        $this->codigoBancoDv = Util::modulo11($codigo, 9, 0, ($this->codigoBanco == '001' ? 1 : 0));
        return $this;
    }

